import React from 'react'

import axios from 'axios';

const ContactForm = () => {
    const [formStatus, setFormStatus] = React.useState('Send')


    const onSubmit = (e) => {
        e.preventDefault();
        setFormStatus('Submitting...');

        const formData = new FormData(e.target);
        axios.post('http://localhost/React_Assignment2/my-react-app/php/saveForm.php', formData)
            .then((response) => {
                if (response.status === 200) {
                    setFormStatus('Submitted');
                } else {
                    setFormStatus('Error');
                }
            })
            .catch((error) => {
                console.error(error);
                setFormStatus('Error');
            });


        axios.post('http://localhost/React_Assignment2/my-react-app/php/saveForm.php', formData)
            .then((response) => {
                console.log(response);
            })
            .catch((error) => {
                console.error(error);
            });

    };



    return (
        <div className="form-container">
            <p className="para"> We would love to hear from you!</p>
            <form className="main">
                <h2 className="heading">Contact Us</h2>
                <div className="form-group">

                    <input type="text" id="first-name" name="first-name" placeholder="First Name" className="style"
                           required/>
                </div>
                <div className="form-group">

                    <input type="text" id="last-name" name="last-name" placeholder="Last Name" className="style"
                           required/>
                </div>
                <div className="form-group">

                    <input type="email" id="email" name="email" placeholder="E-Mail" required className="style"/>
                </div>
                <div className="form-group">

                    <textarea id="query" name="query" rows="4" placeholder="Please enter your query" className="style"
                              required></textarea>
                </div>
                <button type="submit" className="btn">Submit</button>
            </form>
            <img src="images/contact us banner.jpeg" alt="image" className="img-on-form"/>
        </div>
    );
};

export default ContactForm
