import { useState, useEffect } from "react";
import './CartPage.css';
import { useHistory } from "react-router-dom";

function Cart() {
    const [cartItems, setCartItems] = useState([]);
    const history = useHistory();

    useEffect(() => {
        const items = JSON.parse(localStorage.getItem("cartItems")) || [];
        setCartItems(items);
    }, []);

    const handleRemoveItem = (index) => {
        const newCart = [...cartItems];
        newCart.splice(index, 1);
        setCartItems(newCart);

        localStorage.setItem("cartItems", JSON.stringify(newCart));
    };

    const handleIncrement = (index) => {
        const newCart = [...cartItems];
        newCart[index].quantity += 1;
        setCartItems(newCart);

        localStorage.setItem("cartItems", JSON.stringify(newCart));
    };

    const handleDecrement = (index) => {
        const newCart = [...cartItems];
        if (newCart[index].quantity > 1) {
            newCart[index].quantity -= 1;
            setCartItems(newCart);

            localStorage.setItem("cartItem", JSON.stringify(newCart));
        }
    };

    const subTotal = (item) => {
        return item.price * item.quantity;
    };

    const total = cartItems.reduce((acc, item) => {
        return acc + subTotal(item);
    }, 0);

    // Function to navigate to the checkout page
    const navigateToCheckoutPage = () => {
        history.push('/Payment.js');
    }

    return (
        <div className="cart-page">
            <h2>Cart</h2>
            {cartItems.length > 0 ? (
                <table>
                    <thead>
                    <tr>
                        <th>Product</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Subtotal</th>
                        <th>Remove</th>
                    </tr>
                    </thead>
                    <tbody>
                    {cartItems.map((item, index) => (
                        <tr key={index}>
                            <td>{item.name}</td>
                            <td>Rs{item.price}</td>
                            <td>
                                <button onClick={() => handleDecrement(index)}>-</button>
                                {item.quantity}
                                <button onClick={() => handleIncrement(index)}>+</button>
                            </td>
                            <td>Rs{subTotal(item)}</td>
                            <td><button onClick={() => handleRemoveItem(index)}>X</button></td>
                        </tr>
                    ))}
                    <tr className="total">
                        <td colSpan="3"></td><td>Total:</td><td>Rs{total}</td></tr></tbody></table>) : (<p>No items in cart.</p>)}
            {/* Modified checkout button with onClick event */}
            {cartItems.length > 0 && (<button className="checkout-btn" onClick={navigateToCheckoutPage}>Checkout</button>)}
        </div>);
}

export default Cart;
